mapper.select:
select email, pwd, name, role 
from member 
where email=#{email} and pwd = #{pwd}

mapper.update:
update member
set introduce = #{introduce}
where email=#{email}

update member
set pwd = #{pwd}
where email=#{email}

mapper.select:
select pwd
from member
where email=#{email}

select profileImg 
from member
where email=#{email}

mapper.update:
update member
set profileImg = #{profileImg}, profileImgName=#{profileImgName}
where email=#{email}

update member
set profileImg = null, profileImgName=null
where email=#{email}
