mapper.select:
select count(vNo)
from post_vote
where postNo=#{postNo} and email=#{email}

select vNo, picNo, email, postNo
from post_vote
where postNo=#{postNo} and email=#{email}

mapper.update:
update post_vote
set picNo = #{picNo}
where vNo = #{vNo}

mapper.select:
select count(pmLikeNo)
from post_vote
where picNo = #{picNo}
